sqlMap.select:
SELECT a.OCCRRNC_DE AS statsDate , a.TOP_INQIRE_BBSCTT_ID AS mxmmInqireBbsId , b.NTT_SJ AS mxmmInqireBbsNm , b.RDCNT AS maxStatsCo FROM ( SELECT OCCRRNC_DE , TOP_INQIRE_BBSCTT_ID FROM COMTSBBSSUMMARY WHERE 1 = 1 AND OCCRRNC_DE BETWEEN #fromDate# AND #toDate# AND STATS_SE = #statsKind#
AND DETAIL_STATS_SE = #detailStatsKind#
) a, COMTNBBS b WHERE a.TOP_INQIRE_BBSCTT_ID = b.NTT_ID ORDER BY a.OCCRRNC_DE

SELECT a.OCCRRNC_DE AS statsDate , a.MUMM_INQIRE_BBSCTT_ID AS mummInqireBbsId , b.NTT_SJ AS mummInqireBbsNm , b.RDCNT AS minStatsCo FROM ( SELECT OCCRRNC_DE , MUMM_INQIRE_BBSCTT_ID FROM COMTSBBSSUMMARY WHERE 1 = 1 AND OCCRRNC_DE BETWEEN #fromDate# AND #toDate# AND STATS_SE = #statsKind#
AND DETAIL_STATS_SE = #detailStatsKind#
) a, COMTNBBS b WHERE a.MUMM_INQIRE_BBSCTT_ID = b.NTT_ID ORDER BY a.OCCRRNC_DE
